definition.script:
pipeline {
    agent any
    environment {
        date = sh(returnStdout: true, script: 'date +%Y%m%d').trim()
        repo = "${JENKINS_HOME}/userContent"
        SERVER = "jenkins@172.17.0.1"
        R_HOME = "/home/jenkins"
        R_BE_DIR = "${R_HOME}/backend/be/out"
        R_FE_DIR = "${R_HOME}/frontend/fe"
        R_SYS_SUBDIR = "sys/sys"
    }
    stages {
        stage('build BE') {
            when{ expression {params.build_be} }
            steps {
                script {
                    def br = build(
                                job: "edge_be",
                                parameters:[
                                    string(name: 'branch', value: "${params.branch}")
                                ]
                    )
                }
            }
        } //build BE
        stage('build FE') {
            when{ expression {params.build_fe} }
            steps {
                script {
                    def br = build(
                                job: "edge_fe",
                                parameters:[
                                    string(name: 'branch', value: "${params.branch}")
                                ]
                    )
                }
            }
        } //build FE
        stage('build BR') {
            when{ expression {params.build_br} }
            steps {
                script {
                    if ( "${params.branch}"=="" ) {
                        def br = build(
                            job: "q8_buildroot",
                            parameters:[
                                booleanParam(name:'cleanBeforeBuild', value:"${params.cleanBr}"),
                                string(name: 'stage', value: "br"), string(name: 'target', value: "edge")
                            ]
                        )
                    } else {
                        def br = build(
                            job: "q8_buildroot",
                            parameters:[
                                booleanParam(name:'cleanBeforeBuild', value:"${params.cleanBr}"),
                                string(name: 'stage', value: "br"), string(name: 'target', value: "edge"),
                                string(name: 'branch', value: "${params.branch}")
                            ]
                        )
                    }
                }
            }
        } //edge
        stage('build SYS') {
            steps {
                script {
                    if ( params.branch=="" ) {
                        env.repoRoot = "${repo}/${date}/edge"
                    } else {
                        env.repoRoot = "${repo}/edge/${params.branch}"
                    }
                    echo "repoRoot=${repoRoot}"
                    def kernel_exists = fileExists "${repoRoot}/bzImage"
                    def rootfs_exists = fileExists "${repoRoot}/rootfs.tar.xz"
                    if (kernel_exists && rootfs_exists) {
                        //cleanup
                        sh 'rm -rf *'
                        sh 'ssh ${SERVER} "sudo rm -rf ${R_HOME}/backend ${R_HOME}/frontend ${R_HOME}/sys /tmp/bzImage /tmp/rootfs*; mkdir -p ${R_BE_DIR} ${R_FE_DIR}"'
                        echo "Done Cleanup"
                        //deploy component
                        sh 'scp ${repoRoot}/be ${SERVER}:${R_BE_DIR}'
                        sh 'scp ${repoRoot}/www.tgz ${SERVER}:${R_FE_DIR}'
                        sh 'scp ${repoRoot}/bzImage ${SERVER}:/tmp'
                        sh 'scp ${repoRoot}/rootfs.tar.xz ${SERVER}:/tmp'
                        sh 'ssh ${SERVER} "cd ${R_FE_DIR}; tar -xf www.tgz; cd /tmp; xz -d rootfs.tar.xz"'
                        //get source
                        sh 'ssh ${SERVER} "git clone ssh://q8@192.168.1.123/home/q8/Q8_edge/bare/system `dirname ${R_SYS_SUBDIR}`"'
                        if ( params.branch=="" ) {
                            env.ckout = "master"
                        } else {
                            env.ckout = "edge_${params.branch}"
                        }
                        if ( params.image=="usb" || params.image=="both") {
                            sh "ssh ${SERVER} \"cd ${R_SYS_SUBDIR}; git checkout ${ckout}; make jenkins-edge\""
                            sh 'scp ${SERVER}:${R_HOME}/${R_SYS_SUBDIR}/out/images/edge.img.xz ${repoRoot}'
                            env.ckPoint = sh (
                                script: 'ssh ${SERVER} "cd ${R_SYS_SUBDIR}; git log | head -n1 | cut -f2 -d \' \'"',
                                returnStdout: true
                            ).trim()
                            sh "echo usb ${ckPoint} >>${repoRoot}/commit.txt"
                        }
                        if ( params.image=="dom" || params.image=="both") {
                            sh "ssh ${SERVER} \"cd ${R_SYS_SUBDIR}; git checkout ${ckout}; make jenkins-dom\""
                            sh 'scp ${SERVER}:${R_HOME}/${R_SYS_SUBDIR}/out/images/dom.img.xz ${repoRoot}'
                            env.ckPoint = sh (
                                script: 'ssh ${SERVER} "cd ${R_SYS_SUBDIR}; git log | head -n1 | cut -f2 -d \' \'"',
                                returnStdout: true
                            ).trim()
                            sh "echo dom ${ckPoint} >>${repoRoot}/commit.txt"
                        }
                    }
                }
            }
        } //build FE
    } //stages
    post() {
        always {
            cleanWs()
        }
    }
} //pipeline
