definition.script:
pipeline {
    agent any
    environment {
        date = sh(returnStdout: true, script: 'date +%Y%m%d').trim()
        time = sh(returnStdout: true, script: 'date +%H%M').trim()
        keepDirP = "${JENKINS_HOME}/userContent/${date}"
        keepDir = "${keepDirP}/${time}"
        outTar = "out/br/output/images/rootfs.tar"
        edgeKern = "out/br/output/images/bzImage"
        outKern = "tftp/kernel"
        outDiff = "tftp/diff.tgz"
    }
    stages {
        stage('clean & checkout') {
            when { expression { params.cleanBeforeBuild } }
            steps{
                echo "HOME=${HOME}"
                sh 'rm -rf *'
                sh 'git clone ssh://q8vista@192.168.1.160/home/q8vista/Q8_Vista/bare/buildroot'
            }
        } //cleean
        stage('build') {
            steps {
                script {
                    echo "stage= ${params.stage}"
                    if ( params.stage=="br" ) {
                        dir("buildroot/br") {
                            sh "make ${params.target}"
                            if ( params.target=="edge" ) {
                                def exists = fileExists "${outTar}"
                                if (exists) {
                                    sh "xz -zf ${outTar}"
                                    echo "copy ${outTar}.xz to userContent"
                                    //symbolic link is hiden
                                    sh "mkdir -p ${keepDir} ${keepDirP}/edge; cp -a ${outTar}.xz ${keepDir}; cp -a ${outTar}.xz ${keepDirP}/edge"
                                }
                                def exists2 = fileExists "${edgeKern}"
                                if (exists2) {
                                    echo "copy ${edgeKern} to userContent"
                                    sh "mkdir -p ${keepDir} ${keepDirP}/edge; cp -a ${edgeKern} ${keepDir}; cp -a ${edgeKern} ${keepDirP}/edge"
                                }
                            } else { //params.target=="edge", i.e q8
                                def exists = fileExists "${outDiff}"
                                if (exists) {
                                    echo "copy ${outDiff} to userContent"
                                    sh "mkdir -p ${keepDir} ${keepDirP}/q8; cp -a ${outDiff} ${keepDir}; cp -a ${outDiff} ${keepDirP}/q8"
                                }
                            } //params.target=="edge"
                        } //buildroot/br
                    } else {
                        if ( params.stage=="br1" ) {
                            dir("buildroot/br1") {
                                sh "make ${params.target}"
                                def exists = fileExists "${outKern}"
                                if (exists) {
                                    echo "copy kernel to userContent"
                                    sh "mkdir -p ${keepDir} ${keepDirP}/q8; cp -a ${outKern} ${keepDir}; cp -a ${outKern} ${keepDirP}/q8"
                                }
                                def br1Files = ['tftp/default', 'tftp/acpboot.bin', 'tftp/ldlinux.c32']
                                for (int i = 0; i < br1Files.size(); ++i) {
                                    sh "mkdir -p ${keepDirP}; cp -a ${br1Files[i]} ${keepDirP}/q8"
                                }
                            }
                        } else { error "unknown stage ${params.stage}" }
                    }
                }
            }
        } //build
    }
}
