definition.script:
pipeline {
    agent any
    stages {
        stage('build client') {
            when{ expression {params.build_client} }
            steps {
                script {
                    def br = build(job: "q8_client")
                }
            }
        } //build client
        stage('build br1') {
            when{ expression {params.build_br1} }
            steps {
                script {
                    def br = build(
                        job: "q8_buildroot",
                        parameters:[
                            booleanParam(name:'cleanBeforeBuild', value:true),
                            string(name: 'stage', value: "br1"), string(name: 'target', value: "q8")
                        ]
                    )
                }
            }
        } //br1
        stage('build br') {  //edge move i915 driver to /root, do after edge
            when{ expression {params.build_br} }
            steps {
                script {
                    def br = build(
                        job: "q8_buildroot",
                        parameters:[
                            booleanParam(name:'cleanBeforeBuild', value:false),
                            string(name: 'stage', value: "br"), string(name: 'target', value: "q8")
                        ]
                    )
                }
            }
        } //q8
        stage('build server') {
            when{ expression {params.build_server} }
            steps {
                script {
                    def br = build(job: "q8_server")
                }
            }
        } //build server
    } //stages
    post() {
        always {
            cleanWs()
        }
    }
} //pipeline
